import { app, BrowserWindow, ipcMain, WebContents } from 'electron';
import { loadProjectPreview, loadProjectPreviews, loadUser, saveUser, storeNewEmptyProject, initializeNewProject } from './file_utils';
import path from 'path'
import { v4 as uuidv4 } from 'uuid';
import { ProjectCommunication, ProjectListener } from './communication';
import { AppendOnlyLog, Frontier, Operation, uuid } from './append_only_log';
import { Project } from './Project';
// This allows TypeScript to pick up the magic constants that's auto-generated by Forge's Webpack
// plugin that tells the Electron app where to look for the Webpack-bundled app code (depending on
// whether you're running in development or production).
declare const MAIN_WINDOW_WEBPACK_ENTRY: string;
declare const MAIN_WINDOW_PRELOAD_WEBPACK_ENTRY: string;

// Handle creating/removing shortcuts on Windows when installing/uninstalling.
if (require('electron-squirrel-startup')) {
  app.quit();
}

const app_path = app.getPath('userData');
const projects_path = path.join(app_path, 'projects');

const createWindow = (): Electron.WebContents => {
  // Create the browser window.
  const mainWindow = new BrowserWindow({
    width: 800,
    height: 600,
    show: false,
    webPreferences: {
      preload: MAIN_WINDOW_PRELOAD_WEBPACK_ENTRY,
    },
  });

  // mainWindow.maximize(); uncomment to open in fullscreen mode
  mainWindow.show();

  // and load the index.html of the app.
  mainWindow.loadURL(MAIN_WINDOW_WEBPACK_ENTRY);

  // Open the DevTools.
  mainWindow.webContents.openDevTools();
  return mainWindow.webContents;
};

async function runApp(web: Electron.WebContents) {
  let userdata: User = undefined;
  try {
    userdata = loadUser(app_path);
  } catch (e) {
    web.send('switch-scene', 'scene-login')
    const username_p = new Promise<string>((resolve) => {
      ipcMain.on('login-submit', (_, username: string) => {
        resolve(username);
      });
    });
    userdata = { userID: uuidv4(), userName: await username_p };
    saveUser(app_path, userdata);
  }
  let projects = loadProjectPreviews(projects_path);
  const ignored_projects = new Set(projects);
  web.send('update-project-preview', projects);
  ipcMain.on('ignore-project', (_, projectPreview: ProjectPreview) => {
    ignored_projects.add(projectPreview);
  });
  ipcMain.on('add-project', (_, projectPreview: ProjectPreview) => {
    console.log("adding project");
    storeNewEmptyProject(projects_path, projectPreview);
    //projects.unshift(projectPreview);
    projects = loadProjectPreviews(projects_path);
    web.send('update-project-preview', projects);
    ignored_projects.add(projectPreview);
  });

  const pl = new ProjectListener(undefined, 8080);
  // more callbacks
  ipcMain.on('change-port', (_, port: number) => {
    pl.setPort(port);
  });
  ipcMain.on('change-ip-address', (_, ip: string) => {
    pl.setBroadcastIP(ip);
  });
  pl.init((preview, rinfo) => {
    if (![...ignored_projects].every((val) => val.projectID !== preview.projectID)) {
      return;
    }
    web.send('new-project-in-network', preview);
  });
  console.log("pl.broadcast_ip: " + pl.broadcast_ip);
  web.send('update-interface', pl.broadcast_ip, pl.port);
  // TODO: (if there is time) pl.onIfaceChange( (ip, port) => { web.send('update-interface', ip, port) } );
  web.send('switch-scene', 'scene-home');

  for (; ;) {
    const open_project_p = new Promise<uuid>((resolve) => {
      ipcMain.on('open-project', (_, projectID: uuid, projectTitle: string) => {
        ipcMain.removeAllListeners('create-new-project');
        ipcMain.removeAllListeners('open-project');
        resolve(projectID);
      });
      ipcMain.on('create-new-project', (_, projectTitle: string) => {
        const projectID = uuidv4();
        storeNewEmptyProject(projects_path, { projectID, projectTitle });
        initializeNewProject(userdata.userID, userdata.userName, path.join(projects_path, projectID), projectID, projectTitle);
        ipcMain.removeAllListeners('open-project');
        ipcMain.removeAllListeners('create-new-project');
        resolve(projectID);
      });
    });
    // wait for user to open a project
    const project_id = await open_project_p;
    pl.close();
    web.send('switch-scene', 'scene-project');
    await openProject(web, project_id, userdata.userID, userdata.userName); //Hier wird das vorher neu kreierte/ angecklickte Projekt geÃ¶ffnet.
    projects = loadProjectPreviews(projects_path);
    web.send('update-project-preview', projects);
    web.send('switch-scene', 'scene-home');
    pl.open();
    web.send('update-interface', pl.broadcast_ip, pl.port);
  }
}


async function openProject(web: WebContents, projectID: uuid, userID: uuid, userName: string) {
  const projectPreview = loadProjectPreview(projects_path, projectID);
  console.log("PATH: " + projects_path);
  web.send('set-project-title', projectPreview.projectTitle);
  const a = new AppendOnlyLog(path.join(projects_path, projectID, "aol.json"));
  try {
    a.load();
  } catch (e) {
    console.log('append-only log for project ' + projectID + ' doesnt exist yet');
  }
  const p = new Project(projectID, projectPreview.projectTitle, a);
  p.charge();
  const taskViewArr = p.getProjectView();
  web.send('update-project-view', taskViewArr);
  const pc = new ProjectCommunication(8080, undefined, projectID, projectPreview.projectTitle, a, (ops: Operation[]) => {
    console.log("GOT UPDATES");
    p.update(ops, web);
    const frontier: Frontier = new Map<string, number>();
    const resIDs = a._query_missing_entryIDs_ordered(frontier);
    const count = resIDs.length;
    let resIDBool = true;
    console.log("resIDs: " + resIDs);
    if (resIDs) {
      for (let i = 0; i < resIDs.length; i++) {
        if (resIDs[i] === userID) {
          console.log("USer already added");
          resIDBool = false;
        }
      }
      console.log("count: " + count);
      if (count >= 2 && resIDBool) {
        console.log("count: " + count);
        console.log("added member successfully");
        p.addMember(userID, userName, userID, true);
        web.send('update-project-view', p.getProjectView());
      }
    }
  });
  await pc.init();


  ipcMain.on('change-project-task-state', (_, taskID: uuid, newState: string) => {
    p.setTaskStateGUI(userID, taskID, newState);
    web.send('update-project-view', p.getProjectView());
  });

  ipcMain.on('change-assignees', (_, bool: boolean, taskUUID: uuid, personUUID: uuid) => {
    if (bool) {
      p.addTaskAssigneeGUI(userID, taskUUID, personUUID);
    }
    else {
      p.removeTaskAssigneeGUI(userID, taskUUID, personUUID);
    }
    web.send('update-project-view', p.getProjectView());
  });

  ipcMain.on('create-new-task', (_, taskTitle: string) => {
    const taskUUID = uuidv4();
    p.createTask(taskUUID, userID, taskTitle, true);
    web.send('update-project-view', p.getProjectView());
  });

  ipcMain.on('change-username-submit', (_, username: string) => {
    p.changeName(userID, username, true);
    web.send('update-project-view', p.getProjectView());
  });
  pc.messageLoop();
  await new Promise<void>((resolve) => {
    // Beispiel 1: index.ts <- renderer.ts
    ipcMain.once('leave-project', () => {
      pc.messageLoopFalse();
      resolve()
    })
  });
  pc.close();
  p.save();

  // Cleanup von Beispiel 2
  ipcMain.removeAllListeners('change-project-task-state')
  ipcMain.removeAllListeners('create-new-task')
  ipcMain.removeAllListeners('change-assignees')
  ipcMain.removeAllListeners('change-username-submit')
  return;
}

// This method will be called when Electron has finished
// initialization and is ready to create browser windows.
// Some APIs can only be used after this event occurs.
app.whenReady().then(async () => {
  const web = createWindow();
  runApp(web);
})

// Quit when all windows are closed, except on macOS. There, it's common
// for applications and their menu bar to stay active until the user quits
// explicitly with Cmd + Q.
app.on('window-all-closed', () => {
  if (process.platform !== 'darwin') {
    app.quit();
  }
});

app.on('activate', () => {
  // On OS X it's common to re-create a window in the app when the
  // dock icon is clicked and there are no other windows open.
  if (BrowserWindow.getAllWindows().length === 0) {
    createWindow();
  }
});

